#!/bin/bash

echo "Starting haxball container.."

### Delete default config file
configdir=/root/.haxroomie
config=config.js
lockfile=/tmp/haxroomie.lock


if test -f "$lockfile"; then
        rm "$lockfile"
fi

if ! test -d "$configdir"; then
